mapper.select:
SELECT * FROM user_scheduled_common_task
WHERE biz_name = #{bizName}
AND biz_group = #{bizGroup}
AND next_trigger_time < #{end}
AND process != 'processing'
LIMIT #{limit}
FOR UPDATE;

mapper.update:
UPDATE user_scheduled_common_task
SET last_trigger_time = #{lastTriggerTime},
    next_trigger_time = #{nextTriggerTime},
    enable = #{enable}
WHERE id = #{id}
