mapper.select:
SELECT * FROM user_scheduled_common_task
WHERE biz_name = #{bizName}
AND biz_group = #{bizGroup}
AND next_trigger_time < #{end}
AND (process IS NULL OR process != 'processing')
AND enable = '1'
LIMIT #{limit}

SELECT * FROM user_scheduled_common_task
WHERE biz_name = #{bizName}
  AND biz_group = #{bizGroup}
  AND next_trigger_time < #{end}
  AND (process IS NULL OR process != 'processing')
  AND enable = '1'
  AND MOD(id, #{shardCount}) = #{shardIndex}
    LIMIT #{limit}

mapper.update:
UPDATE user_scheduled_common_task SET process = 'processing', locked_at = UNIX_TIMESTAMP(NOW()) * 1000 -- 毫秒级 WHERE id IN
#{id}
AND (process IS NULL OR process != 'processing')

UPDATE user_scheduled_common_task
SET last_trigger_time = #{lastTriggerTime},
    next_trigger_time = #{nextTriggerTime},
    enable = #{enable}
WHERE id = #{id}

update.foreach:
UPDATE user_scheduled_common_task
SET last_trigger_time = #{item.lastTriggerTime},
next_trigger_time = #{item.nextTriggerTime},
enable = #{item.enable}
WHERE id = #{item.id}
